export class ManagementComponent {
    constructor(msal_service, apiConnection, service) {
        this.msal_service = msal_service;
        this.apiConnection = apiConnection;
        this.service = service;
        this.showMenuBar = false;
        // selectedPage: string = "createNewCycle";
        this.page = 'home';
        this.lastSortExpand = 0;
        this.previewPopUp = false;
        this.information = [{ font: "11", text: "11" }];
        this.buttonClicked = false;
        this.inputControl = new FormControl();
        this.showList = false;
        this.selectedAvionicsItems = [];
        this.selectedMaintenaceItems = [];
        // basesr: any;
        // courses;
        // indexOutput$: Observable<any[]> = this.apiConnection.GetRequest("https://index-bislat-back.azurewebsites.net/Course");
        // selectedItems: [{ gender: string, courses: any[] }];
        this.newCycle = new FormGroup({
            name: new FormControl('', Validators.required),
            selectionForm: new FormControl(false)
        });
        this.sorts = this.createSortArray();
        // this.courses = this.apiConnection.GetRequest("https://index-bislat-back.azurewebsites.net/Course");
        // this.selectedItems = [{ gender: 'Avionics', courses: [] }];
        // this.selectedItems.push({ gender: 'Maintenace', courses: [] });
        this.indexOutput$ = this.inputControl.valueChanges.pipe(service.getListCourses());
    }
    ngOnInit() { }
    isManager() { return this.msal_service.isManager(); }
    addNewItem(event) {
        if (!this.selectedAvionicsItems.includes(event) && !this.selectedMaintenaceItems.includes(event)) {
            switch (event.gender) {
                case 'Avionics':
                    this.selectedAvionicsItems.push(event);
                    break;
                case 'Maintenace':
                    this.selectedMaintenaceItems.push(event);
                    break;
            }
        }
    }
    deleteItem(course, gender) {
        if (gender === 'Avionics') {
            const index = this.selectedAvionicsItems.indexOf(course);
            this.selectedAvionicsItems.splice(index, 1);
        }
        else if (gender === 'Maintenace') {
            const index = this.selectedMaintenaceItems.indexOf(course);
            this.selectedMaintenaceItems.splice(index, 1);
        }
    }
    onBlur() {
        setTimeout(() => {
            this.showList = false;
        }, 80);
    }
    listOnFocus() {
        this.showList = true;
    }
    sortOnFocus(index) {
        this.sorts[index].isExpand = !this.sorts[index].isExpand;
        if (this.lastSortExpand != index)
            this.sorts[this.lastSortExpand].isExpand = false;
        this.lastSortExpand = index;
    }
    createPreviewInformation() {
        let informationn = [{ font: "title", text: this.newCycle.controls['name'].value }];
        if (this.selectedMaintenaceItems.length) {
            informationn.push({ font: "subtitle", text: "אחזקה מתכת/חשמל" });
        }
        for (let i = 0; i < this.selectedMaintenaceItems.length; i++) {
            let s = this.selectedMaintenaceItems[i].courseNumber + "- " + this.selectedMaintenaceItems[i].courseName;
            informationn.push({ font: "text", text: s });
        }
        if (this.selectedAvionicsItems.length) {
            informationn.push({ font: "subtitle", text: "אוויוניקה" });
        }
        for (let i = 0; i < this.selectedAvionicsItems.length; i++) {
            let s = this.selectedAvionicsItems[i].courseNumber + "- " + this.selectedAvionicsItems[i].courseName;
            informationn.push({ font: "text", text: s });
        }
        if (this.newCycle.controls['selectionForm'].value === true) {
            informationn.push({ font: "subtitle", text: "נפתחת אפשרות למלא שאלון העדפות" });
        }
        else {
            informationn.push({ font: "subtitle", text: "לא נפתחת אפשרות למלא שאלון העדפות" });
        }
        return informationn;
    }
    // ---------------------------------------- creating ----------------------------------------
    createSortArray() {
        const sortsName = this.apiConnection.GetRequest("https://index-bislat-back.azurewebsites.net/Sort");
        let sortArr = [];
        for (let i = 0; i < sortsName.length; i++) {
            sortArr.push({ name: sortsName[i].name, isExpand: false });
        }
        return sortArr;
    }
    createSycleInterface() {
        if (this.newCycle.valid && (this.selectedAvionicsItems.length + this.selectedMaintenaceItems.length > 2)) {
            this.information = this.createPreviewInformation();
            this.item = {
                name: this.newCycle.controls['name'].value,
                courses: [...this.service.findCourseNumber(this.selectedAvionicsItems), ...this.service.findCourseNumber(this.selectedMaintenaceItems)]
            };
            this.previewPopUp = true;
        }
    }
    deleteCycle(cycleName) {
        this.apiConnection.DeleteRequest('https://index-bislat-back.azurewebsites.net/Sort/' + cycleName);
        this.sorts = this.createSortArray();
    }
    cancelSort() {
        this.page = "home";
        this.selectedAvionicsItems = [];
        this.selectedMaintenaceItems = [];
        this.newCycle.controls['name'].setValue('');
        this.inputControl.setValue('');
        this.newCycle.controls['selectionForm'].setValue(false);
        this.buttonClicked = false;
    }
    // ---------------------------------------- validators ----------------------------------------
    inputValidator() {
        return (control) => {
            if (!this.selectedAvionicsItems.length && !this.selectedMaintenaceItems.length) {
                return { forbiddenName: { value: control.value } };
            }
            return null;
        };
    }
}
