  }

  ngOnInit() {}

  isManager() {
    return this.msal_service.isManager();
  }

  addCourse(event) {
    if (!this.selectedAvionicsItems.includes(event) && !this.selectedMaintenaceItems.includes(event)) {
      switch (event.gender) {
        case 'Avionics':
          this.selectedAvionicsItems.push(event);
          break;

        case 'Maintenace':
          this.selectedMaintenaceItems.push(event);
          break;
      }
    }
  }

  deleteCourse(course, gender) {
    this.lastSortExpand = 0;

    if (gender === 'Avionics') {
      const index = this.selectedAvionicsItems.indexOf(course);
      this.selectedAvionicsItems.splice(index, 1);
    } else if (gender === 'Maintenace') {
      const index = this.selectedMaintenaceItems.indexOf(course);
      this.selectedMaintenaceItems.splice(index, 1);
    }
  }

  deleteCycle(cycleName, index) {
    this.apiConnection.DeleteRequest('https://index-bislat-back.azurewebsites.net/Sort/' + cycleName);
    this.sortElemntsProperties.splice(index, 1);
  }

  clearPage() {
    this.page = {
      text: 'מחזורי מיון',
      url: 'sorts'
    };
    this.selectedAvionicsItems = [];
    this.selectedMaintenaceItems = [];
    this.newCycle.controls['name'].setValue('');
    this.inputControl.setValue('');
    this.newCycle.controls['openSelectionForm'].setValue(false);
    this.buttonClicked = false;
    this.sortElemntsProperties[this.lastSortExpand].isExpand = false;
    this.lastSortExpand = 0;
  }

  onBlur() {
    setTimeout(() => {
      this.showList = false;
    }, 80);
  }

  listOnFocus() {
    this.showList = true;
  }

  sortOnFocus(index, varType) {
    switch (varType) {
      case 'archives':
        this.sortElemntsProperties[index].isExpand = !this.sortElemntsProperties[index].isExpand;
        if (this.lastSortExpand != index) this.sortElemntsProperties[this.lastSortExpand].isExpand = false;
        this.lastSortExpand = index;
        break;

      case 'sorts':
        this.archivesSorts[index].isExpand = !this.archivesSorts[index].isExpand;
        if (this.lastSortExpand != index) this.archivesSorts[this.lastSortExpand].isExpand = false;
        this.lastSortExpand = index;
        break;
    }
  } // ---------------------------------------- creating ----------------------------------------


  getListPropertiesOfSortElements(varType) {
    const sortsName = this.apiConnection.GetRequest("https://index-bislat-back.azurewebsites.net/Sort");
    let sortsProperties = [];

    for (let i = 0; i < sortsName.length; i++) {
      switch (varType) {
        case 'sorts':
          if (sortsName[i].status != 0) {
            sortsProperties.push({
              name: sortsName[i].name,
              isExpand: false
            });
          }

          break;

        case 'archive':
          sortsProperties.push({
            name: sortsName[i].name,
            isExpand: false
          });
          break;
      }
    }

    console.log(sortsProperties);
    return sortsProperties;
  }

  createCycleInterface() {
    if (this.newCycle.valid && this.coursesValidator) {
      this.previewPageInformation = this.createPreviewInformation();
      this.item = {
        name: this.newCycle.controls['name'].value,
        status: this.getCycleNumberStatus(),
        courses: [...this.service.findCourseNumber(this.selectedAvionicsItems), ...this.service.findCourseNumber(this.selectedMaintenaceItems)]
      };
      this.previewPopUp = true;
    }
  }

  createPreviewInformation() {
    let informationn = [{
      font: "title",
      text: this.newCycle.controls['name'].value
    }];

    if (this.selectedMaintenaceItems.length) {
      informationn.push({
        font: "subtitle",
        text: "אחזקה מתכת/חשמל"
      });
    }

    for (let i = 0; i < this.selectedMaintenaceItems.length; i++) {
      let s = this.selectedMaintenaceItems[i].courseNumber + "- " + this.selectedMaintenaceItems[i].courseName;
      informationn.push({
        font: "text",
        text: s
      });
    }

    if (this.selectedAvionicsItems.length) {
      informationn.push({
        font: "subtitle",
        text: "אוויוניקה"
      });
    }

    for (let i = 0; i < this.selectedAvionicsItems.length; i++) {
      let s = this.selectedAvionicsItems[i].courseNumber + "- " + this.selectedAvionicsItems[i].courseName;
      informationn.push({
        font: "text",
        text: s
      });
    }

    if (this.newCycle.controls['showSortOnWeb'].value === true) {
      switch (this.newCycle.controls['openSelectionForm'].value) {
        case true:
          informationn.push({
            font: "subtitle",
            text: "המחזור יוצג באתר ותיפתח אפשרות למלא שאלון העדפות"
          });
          break;

        case false:
          informationn.push({
            font: "subtitle",
            text: "המחזור יוצג באתר ולא יינתן למלא שאלון העדפות"
          });
          break;
      }
    } else {
      informationn.push({
        font: "subtitle",
        text: "המחזור לא יוצג באתר וישמר בארכיון!"
      });
    }

    return informationn;
  } // ---------------------------------------- validators ----------------------------------------


  inputValidator() {
    return control => {
      if (!this.selectedAvionicsItems.length && !this.selectedMaintenaceItems.length) {
        return {
          forbiddenName: {
            value: control.value
          }
        };
      }

      return null;
    };
  }

  coursesValidator() {
    return (!this.newCycle.controls['name'].valid || this.selectedAvionicsItems.length < 3 && this.selectedAvionicsItems.length > 0 || this.selectedMaintenaceItems.length < 3 && this.selectedMaintenaceItems.length > 0 || this.selectedAvionicsItems.length === 0 && this.selectedMaintenaceItems.length === 0) && this.buttonClicked;
  }

  getCycleNumberStatus() {
    let selectionFormStatus = this.newCycle.controls['openSelectionForm'].value;
